import{_ as d,c as e,o as a,ah as i}from"./chunks/framework.Dgw6jcLr.js";const n=JSON.parse('{"title":"050、CPU、内存、硬盘三大件，MySQL服务器该如何选择？","description":"","frontmatter":{},"headers":[],"relativePath":"05.架构/架构900讲/运维.md","filePath":"05.架构/架构900讲/运维.md","lastUpdated":null}'),r={name:"05.架构/架构900讲/运维.md"};function l(o,t,p,s,_,c){return a(),e("div",null,[...t[0]||(t[0]=[i('<h1 id="_050、cpu、内存、硬盘三大件-mysql服务器该如何选择" tabindex="-1">050、CPU、内存、硬盘三大件，MySQL服务器该如何选择？ <a class="header-anchor" href="#_050、cpu、内存、硬盘三大件-mysql服务器该如何选择" aria-label="Permalink to “050、CPU、内存、硬盘三大件，MySQL服务器该如何选择？”">​</a></h1><p><a href="https://www.bilibili.com/video/BV1KP4y1a7EX?spm_id_from=333.788.videopod.sections&amp;vd_source=6c29dc1237e5430deed6d6a26952c755" target="_blank" rel="noreferrer">https://www.bilibili.com/video/BV1KP4y1a7EX?spm_id_from=333.788.videopod.sections&amp;vd_source=6c29dc1237e5430deed6d6a26952c755</a></p><p>一、CPU</p><ul><li>对于并发比较高的场景，CPU的数量比频率更重要。</li><li>对于CPU密集型场景和复杂SQL则频率越高越好。</li></ul><p>二、内存</p><ul><li>理想的选择是服务器内存大于数据量，直接全部加载到内存，小公司适用。</li><li>内存比数据小，缓存热点数据，保证内存覆盖</li><li>内存频率越高处理速度越快</li></ul><p>三、硬盘</p><ul><li><p>固态&gt;混合&gt;机械</p></li><li><p>磁盘阵列RAID选择</p><table tabindex="0"><thead><tr><th>等级</th><th>特点</th><th>是否冗余</th><th>盘数</th><th>读</th><th>写</th><th>备注</th></tr></thead><tbody><tr><td>raid0</td><td>便宜、快速、危险</td><td>否</td><td>N</td><td>快</td><td>快</td><td>将磁盘串联，提高性能</td></tr><tr><td>raid1</td><td>高速读取、安全</td><td>是</td><td>2</td><td>快</td><td>快</td><td>将磁盘进行备份</td></tr><tr><td>raid5</td><td>安全，折中</td><td>是</td><td>N+1</td><td>快</td><td>快</td><td>奇偶磁盘校验，2块都失效，则数据失效，利用一块记录奇偶磁盘位置</td></tr><tr><td>raid10</td><td>贵、安全</td><td>是</td><td>2N</td><td>快</td><td>快</td><td>先做raid1，再做raid0。最后进行串联</td></tr></tbody></table></li></ul>',8)])])}const m=d(r,[["render",l]]);export{n as __pageData,m as default};
