import{_ as l,c as e,o as i,ah as r}from"./chunks/framework.DVT8N-ZV.js";const _=JSON.parse('{"title":"生活小技巧","description":"","frontmatter":{},"headers":[],"relativePath":"09.杂记/生活小技巧.md","filePath":"09.杂记/生活小技巧.md","lastUpdated":null}'),t={name:"09.杂记/生活小技巧.md"};function o(h,a,n,d,s,c){return i(),e("div",null,[...a[0]||(a[0]=[r('<h1 id="生活小技巧" tabindex="-1">生活小技巧 <a class="header-anchor" href="#生活小技巧" aria-label="Permalink to “生活小技巧”">​</a></h1><h3 id="天津特产" tabindex="-1">天津特产 <a class="header-anchor" href="#天津特产" aria-label="Permalink to “天津特产”">​</a></h3><ol><li>十八街麻花（桂发祥铁盒）</li><li>小八件（桂发祥）</li><li>果仁张</li><li>二厂酱货</li><li>泥人张</li><li>杨柳青年画</li><li>桂顺斋点心</li></ol><h2 id="养猫" tabindex="-1">养猫 <a class="header-anchor" href="#养猫" aria-label="Permalink to “养猫”">​</a></h2><h3 id="猫癣治疗" tabindex="-1">猫癣治疗 <a class="header-anchor" href="#猫癣治疗" aria-label="Permalink to “猫癣治疗”">​</a></h3><blockquote><p>猫：用伊曲康挫，按体重喂</p><p>人：用盐酸特比萘芬</p><p>环境：杜邦卫可</p><p>衣物棉织品：凯妮汀</p></blockquote><h3 id="马拉色菌" tabindex="-1">马拉色菌 <a class="header-anchor" href="#马拉色菌" aria-label="Permalink to “马拉色菌”">​</a></h3><blockquote><p>复方制霉菌素软膏</p></blockquote><h2 id="租房-买房" tabindex="-1">租房|买房 <a class="header-anchor" href="#租房-买房" aria-label="Permalink to “租房|买房”">​</a></h2><ol><li><p>窗户尽量不靠近街道，最好在小区中心</p><p>尽管有阳台层隔离，但是周末休息开窗的时候，会有不小的噪音。</p></li><li><p>步梯小区，尽量低楼层，7层楼选5层及以下</p><ul><li>不方便搬运东西和上下楼，会减少出门的频率。</li><li>顶层容易漏水和冬冷夏热。</li></ul></li></ol><h2 id="白色衣服被染色" tabindex="-1">白色衣服被染色 <a class="header-anchor" href="#白色衣服被染色" aria-label="Permalink to “白色衣服被染色”">​</a></h2><p>84消毒液（强碱）+白醋（酸）</p><p>按比例兑水，用棉签蘸取，擦拭染色区域，可去除，然后用清水多洗几遍。</p><h2 id="煎药" tabindex="-1">煎药 <a class="header-anchor" href="#煎药" aria-label="Permalink to “煎药”">​</a></h2><ol><li>浸泡1个小时左右</li><li>没过药材3-5厘米，2个手指。</li><li>大火煮开转小火30分钟</li><li>倒出200ml</li><li>立即加水接着二煎，大火转小火20分钟</li><li>倒出200ml，跟一煎的混合在一起，分2次服用。（一煎药性大，混合使用，可平衡药性。）</li></ol>',15)])])}const b=l(t,[["render",o]]);export{_ as __pageData,b as default};
